// MapView.js
import React, { useState, useEffect } from 'react';
import L from 'leaflet';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import { getWalkingRoute } from './RouteService'; // Import the getWalkingRoute function

// Import marker images
import markerIcon2x from 'leaflet/dist/images/marker-icon-2x.png';
import markerIcon from 'leaflet/dist/images/marker-icon.png';
import markerShadow from 'leaflet/dist/images/marker-shadow.png';

// Fix for default icon issues with Webpack
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

const MapView = ({ parkingLots, startLocation, classLocation }) => {
  const [route, setRoute] = useState(null);
  const [routeInfo, setRouteInfo] = useState(null);
  const [selectedParkingLot, setSelectedParkingLot] = useState(null);

  useEffect(() => {
    const fetchRoute = async () => {
      if (startLocation && classLocation) {
        try {
          const data = await getWalkingRoute(startLocation, classLocation);
          const coordinates = data.features[0].geometry.coordinates.map(
            (coord) => [coord[1], coord[0]] // Convert from [lng, lat] to [lat, lng]
          );
          setRoute(coordinates);
  
          // Extract distance and duration
          const distance = data.features[0].properties.summary.distance; // in meters
          const duration = data.features[0].properties.summary.duration; // in seconds
  
          setRouteInfo({ distance, duration });
        } catch (error) {
          console.error('Error fetching route:', error);
        }
      } else {
        setRoute(null);
        setRouteInfo(null);
      }
    };
  
    fetchRoute();
  }, [startLocation, classLocation]);

  return (
    <MapContainer center={classLocation || [43.000, -78.787]} zoom={15} style={{ height: '80vh', width: '100%' }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

      {/* Render starting location marker */}
      {startLocation && (
        <Marker position={startLocation}>
          <Popup>Starting Location</Popup>
        </Marker>
      )}

      {/* Render class location marker */}
      {classLocation && (
        <Marker position={classLocation}>
          <Popup>Destination</Popup>
        </Marker>
      )}

      {/* Render the route polyline if route is available */}
      {route && <Polyline positions={route} color="blue" />}

      {/* Display route information if available */}
      {routeInfo && (
        <div
          className="route-info"
          style={{
            position: 'absolute',
            bottom: '10px',
            left: '10px',
            backgroundColor: 'rgba(255, 255, 255, 0.8)',
            padding: '10px',
            borderRadius: '5px',
          }}
        >
          <p>
            Distance: {(routeInfo.distance / 1000).toFixed(2)} km<br />
            Estimated Time: {(routeInfo.duration / 60).toFixed(0)} minutes
          </p>
        </div>
      )}
    </MapContainer>
  );
};

export default MapView;
